                    if (grupoValidacion) {
                        if (!jElemento.dataset("grupo") || jElemento.dataset("grupo") !== grupoValidacion ||
                            jElemento.dataset("ign") === "S") {
                            return true;
                        }
                    }
                    var longitudAnterior = mensajes2.length;
