            var columnaCorrelativo = jtbl.dataset("agrattrcorr") || "corr";
            var correlativoInicial = Number(jtbl.dataset("agrcorini")) || -1;
            var textoDetalle = jtbl.dataset("agrtxtdet") || "detalle";
            var jtfoot = jtbl.find("tfoot");
