jQuery.fn.dataset = function (name, value) {
    var PREFIX = "data-";
    if (value !== undefined) {
        return this.attr(PREFIX + name, value);
    }
    if (this.length == 0) {
        return null;
    }
    if (this[0].dataset) {
        return this[0].dataset[name];
    }
    return this.attr(PREFIX + name);
};
